sys_script_include.script:
var noteSearch = Class.create();
noteSearch.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
	 noteReturn: function(){
		 var phone = this.getParameter('sysparm_phone');
		 var gr = new GlideRecord('x_164970_phone_cmdb');
		 gr.addQuery('u_phone',phone);
		 gr.query();

		 if (gr.next()) {
			 var notes = gr.u_notes.getDisplayValue();
			 this.getRootElement().setAttribute('u_notes', notes);
		 }
	 },
	type: 'noteSearch'
});
